import React, { Component } from 'react'
import './Item.css'

export default class Item extends Component {
  // 因为css与js尽量分开 不要在js中写直接操作样式的内容
  state = {mouse:false}

  handleMouse = (flag) => {
    return () => {
      // 表示鼠标已经进入
      if(flag) {
        this.setState({mouse: flag})
      } else {
        this.setState({mouse: flag})
      }
    }
  }
  
  handleChecked = (id) => {
    return (event) => {
      // 可以通过event.target.checked拿到是否勾选的状态
      // 且可以通过传入的id拿到待办事项的id
      console.log(event.target.checked)
      
    }
    
  }
  render() {
    const {id,name,done,updateTodo} = this.props
    const {mouse} = this.state
    return (
      // 鼠标是否进入
      // 这里直接写内联样式
      <li style={{backgroundColor: mouse===true ? '#ddd':'#fff'}} onMouseEnter={this.handleMouse(true)} onMouseLeave={this.handleMouse(false)}>
        <label>
          {/* 添加上是否勾选之后对状态中数据的操作 */}
          <input onChange={this.handleChecked(id)} type="checkbox" defaultChecked={done} />
          <span>{name}</span>
        </label>
        <button className="btn btn-danger" style={{display: mouse === true ? 'block':'none'}}>删除</button>
      </li>
    )
  }
}
