    // const content = DetailData.find((data) => {
    //   return detail.id === data.id
    // })

    return (
      <div>
        Id: {detail.id} <br/>
        Title: {detail.title} <br/>
        {/* Content: {content.content} <br/> */}
      </div>
    )
  }
}
